/* pagination 페이지 링크 function */

/* 
 * 페이지 갯수 변경 후 submit
 */
function pageSizeChange(){
	$("#selPageSize").val(document.listForm.selPageSize.value);
	document.listForm.action = "";
   	document.listForm.submit();
}
/*
 * 화면 로드시 페이지 갯수 설정
 */
window.onload = function(){
	$("#selPageSize").val('10');
}
$(function() {
	if('' != '')
		$("#selSearchCondition").val('');
});

